def unique_elements(arr1, arr2):
    res = [] 
    i = j = 0
    while i < len(arr1) and j < len(arr2): 
        if arr1[i] == arr2[j]: 
            i += 1
            j += 1
        elif arr1[i] < arr2[j]: 
            res.append(arr1[i]) 
            i += 1
        else: 
            res.append(arr2[j]) 
            j += 1
    res.extend(arr1[i:]) 
    res.extend(arr2[j:]) 
    return list(set(res))
