def longest_substring(s):
    length = len(s)
    current_length = 1
    last_occur= {s[0]:0 }
    max_length = 0
    i = 0 
    while i < length:
        if(s[i] in last_occur):
            current_length = i - last_occur[s[i]]
            if(current_length > max_length):
                max_length = current_length
            last_occur[s[i]] = i
        else:
            last_occur[s[i]] = i
            current_length += 1
            if(current_length > max_length):
                max_length = current_length
        i += 1
    return max_length

