#Implement a function that takes in 2 strings and returns a new string consisting of alternating characters
def alt_string(str1, str2):
    alt_str = "" 
    length = len(str1) if len(str1) <= len(str2) else len(str2)

    for i in range(length):
        alt_str += str1[i] + str2[i]

    if len(str1) > len(str2):
        alt_str += str1[length:]
    elif len(str2) > len(str1):
        alt_str += str2[length:]

    return alt_str
