def find_path(array, source, dest):
    path = [source]
    sx, sy = source
    while path[-1] != dest:
        dx, dy = path[-1]
        if dx > 0 and array[dx - 1][dy] == 0:
            path.append([dx-1, dy])
        elif dy > 0 and array[dx][dy-1] == 0:
            path.append([dx, dy-1])
        elif dx < len(array)-1 and array[dx + 1][dy] == 0:
            path.append([dx+1, dy])
        elif dy < len(array[0])-1 and array[dx][dy+1] == 0:
            path.append([dx, dy+1])
    return path
