def mode(nums):
    countDict = {}
    for num in nums:
        if num in countDict:
            countDict[num] += 1
        else:
            countDict[num] = 1
    maxValue = 0
    maxKey = -1
    for key, value in countDict.items():
        if value > maxValue:
            maxValue = value
            maxKey = key
    return maxKey

def median(nums):
    nums.sort()
    if len(nums) % 2 == 0:
        return (nums[len(nums)//2] + nums[len(nums)//2-1])/2
    else:
        return nums[(len(nums)-1)//2]

