def join_dictionaries(d1, d2):
    ''' 
    This function joins two dictionaries by adding their values with
    the same key
    '''
    result = {}
    for k1, v1 in d1.items():
        result[k1] = v1

    for k2, v2 in d2.items():
        if k2 in result.keys():
            result[k2] += v2
        else:
            result[k2] = v2

    return result

if __name__ == '__main__':
