def is_anagram(string1, string2):

    # remove whitespaces
    string1 = string1.replace(" ","")
    string2 = string2.replace(" ","")

    # check length
    if len(string1) != len(string2):
        return False

    # create counters
    counter = {}
    for letter in string1:
        if letter in counter :
            counter[letter] += 1
        else:
            counter[letter] = 1

    for letter in string2:
        if letter in counter:
            counter[letter] -= 1
        else:
            return False

    # Check all values are 0
    for key in counter:
        if counter[key] != 0:
            return False

    return True
