def DFS(root):
    S = []
    visited = []

    S.append(root)

    while len(S) > 0:
        node = S.pop()
        visited.append(node)

        for vertex in node.get_adjacent_vertices():
            if vertex not in visited:
                S.append(vertex)
    return visited
