def LongestCommonSubsequence(str1, str2):
    n = len(str1)
    m = len(str2)
    t = [[0 for j in range(m+1)] for i in range(n+1)]

    for i in range(1, n+1): 
        for j in range(1, m+1):  
            if str1[i-1] == str2[j-1]: 
                t[i][j] = 1 + t[i-1][j-1] 
            else: 
                t[i][j] = max(t[i-1][j], t[i][j-1])   
    return t[n][m]
