class Stack():
    def __init__(self): 
        self.stack = []

    # Inserts an element in the stack
    def push(self, item):
        self.stack.append(item)

    # Return the maximum element in a stack
    def max_stack(self): 
        max_elem = self.stack[0] 
        for i in range(1,len(self.stack)): 
            if self.stack[i]>max_elem:
                    max_elem = self.stack[i] 
        return max_elem
