def operations(string):
    char_set = set(string)
    while len(char_set) > 1:
        char_arr = list(char_set)
        x = char_arr[0]
        y = char_arr[1]
        if x == y:
            break
        elif x > y:
            new_val = x/y
            for i in range(len(string)):
                if string[i] == x:
                    string[i] = new_val
        else:
            new_val = y/x
            for i in range(len(string)):
                if string[i] == x:
                    string[i] = new_val
        char_set = set(string)
    return len(char_set)-1
