		if self.is_empty(): 
			return None
		return self.stack.pop() 

	# This method returns the topmost element without removing it 
	def peek(self): 
		if self.is_empty(): 
			return None
		return self.stack[-1] 

	# This method returns true or false depending on whether the stack is empty or not 
	def is_empty(self): 
		return len(self.stack) == 0
