def longest_no_repeat(s):
    max_length = 0
    length = 0
    seen = dict() 

    for i in range(len(s)):
        if s[i] not in seen:
            length += 1
            seen[s[i]] = i 
        else:
            if length > max_length:
                max_length = length
            length = i - seen[s[i]]
            for j in seen:
                if seen[j] < seen[s[i]]:
                    del seen[j]
            seen[s[i]] = i

    if length > max_length:
        max_length = length

    return max_length

