def sieveOfEratosthenes(n):
    prime_numbers = [True]*(n+1) 
    prime_numbers[0] = False
    prime_numbers[1] = False

    for i in range(2, int(math.sqrt(n)+1)): 
        if (prime_numbers[i]): 
            for j in range(i*i, n+1, i): 
                prime_numbers[j] = False

    prime_numbers_list = []
    for k in range(n+1): 
        if prime_numbers[k]: 
            prime_numbers_list.append(k)

